const travelStyles = [
  {
    id: 'family',
    title: '아이와 함께',
    description: '아이들이 좋아할 만한 장소와 활동을 중심으로 한 여행',
    icon: <FaUsers />
  },
  {
    id: 'couple',
    title: '커플 여행',
    description: '로맨틱하고 특별한 추억을 만들 수 있는 여행',
    icon: <FaHeart />
  },
  {
    id: 'solo',
    title: '나홀로',
    description: '자유롭고 독립적인 여행을 즐길 수 있는 코스',
    icon: <FaMapMarkedAlt />
  },
  {
    id: 'group',
    title: '친구들과',
    description: '함께 즐기고 추억을 나눌 수 있는 여행',
    icon: <FaUsers />
  }
];

const keywords = [
  '자연', '액티비티', '힐링', '맛집', '역사', '체험', '쇼핑', '문화'
];

const destinations = [
  {
    id: 1,
    name: '제주도',
    description: '자연과 힐링을 동시에 즐길 수 있는 아름다운 섬',
    tags: ['자연', '힐링', '맛집']
  },
  {
    id: 2,
    name: '부산',
    description: '바다와 도시의 매력을 모두 느낄 수 있는 곳',
    tags: ['맛집', '문화', '액티비티']
  },
  {
    id: 3,
    name: '강릉',
    description: '아름다운 해안선과 맛있는 커피를 즐길 수 있는 곳',
    tags: ['힐링', '맛집', '자연']
  }
];

const HomePage: React.FC = () => {
  const [selectedKeywords, setSelectedKeywords] = useState<string[]>([]);

  const handleKeywordClick = (keyword: string) => {
    setSelectedKeywords(prev => 
      prev.includes(keyword) 
        ? prev.filter(k => k !== keyword)
        : [...prev, keyword]
    );
  };

  return (
    <HomeContainer>
      <HeroSection>
        <HeroContent>
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <HeroTitle>나만의 여행을 시작해보세요</HeroTitle>
            <HeroSubtitle>
              AI가 추천하는 맞춤형 여행지와 함께 특별한 추억을 만들어보세요
            </HeroSubtitle>
          </motion.div>
          
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <CTAButtons>
              <PrimaryButton to="/travel-test">여행 성향 진단하기</PrimaryButton>
              <SecondaryButton to="/travel-test">직접 선택하기</SecondaryButton>
            </CTAButtons>
          </motion.div>
        </HeroContent>
      </HeroSection>

      <TravelStyleSection>
        <SectionTitle>어떤 여행을 원하시나요?</SectionTitle>
        <StyleGrid>
          {travelStyles.map((style, index) => (
            <StyleCard
              key={style.id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ scale: 1.05 }}
            >
              <StyleIcon>{style.icon}</StyleIcon>
              <StyleTitle>{style.title}</StyleTitle>
              <StyleDescription>{style.description}</StyleDescription>
            </StyleCard>
          ))}
        </StyleGrid>
      </TravelStyleSection>

      <KeywordSection>
        <SectionTitle>관심 있는 키워드를 선택하세요</SectionTitle>
        <KeywordGrid>
          {keywords.map((keyword, index) => (
            <KeywordCard
              key={keyword}
              $selected={selectedKeywords.includes(keyword)}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ scale: 1.05 }}
              onClick={() => handleKeywordClick(keyword)}
            >
              {keyword}
            </KeywordCard>
          ))}
        </KeywordGrid>
      </KeywordSection>

      <RecommendationSection>
        <SectionTitle>추천 여행지</SectionTitle>
        <DestinationGrid>
          {destinations.map((destination, index) => (
            <DestinationCard
              key={destination.id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ scale: 1.02 }}
            >
              <DestinationImage>
                {destination.name}
              </DestinationImage>
              <DestinationContent>
                <DestinationTitle>{destination.name}</DestinationTitle>
                <DestinationDescription>{destination.description}</DestinationDescription>
                <DestinationTags>
                  {destination.tags.map(tag => (
                    <Tag key={tag}>{tag}</Tag>
                  ))}
                </DestinationTags>
              </DestinationContent>
            </DestinationCard>
          ))}
        </DestinationGrid>
      </RecommendationSection>
    </HomeContainer>
  );
};

export default HomePage;
