const TravelTestPage: React.FC = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState<{ [key: number]: string }>({});
  const [showResult, setShowResult] = useState(false);
  const navigate = useNavigate();

  const handleOptionSelect = (optionId: string) => {
    setAnswers(prev => ({
      ...prev,
      [currentQuestion]: optionId
    }));
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    } else {
      setShowResult(true);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const getResult = () => {
    const typeCounts: { [key: string]: number } = {};
    
    Object.values(answers).forEach(answerId => {
      const question = questions.find(q => q.id === currentQuestion + 1);
      const option = question?.options.find(o => o.id === answerId);
      if (option) {
        typeCounts[option.type] = (typeCounts[option.type] || 0) + 1;
      }
    });

    const dominantType = Object.entries(typeCounts).sort(([,a], [,b]) => b - a)[0]?.[0];
    
    return {
      type: dominantType || "균형형",
      description: `${dominantType || "균형"}을 중시하는 여행자입니다.`,
      tags: Object.keys(typeCounts).slice(0, 3)
    };
  };

  const handleStartPlanning = () => {
    navigate('/');
  };

  if (showResult) {
    const result = getResult();
    
    return (
      <TestContainer>
        <TestCard
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
        >
          <ResultContainer>
            <ResultTitle>테스트 결과</ResultTitle>
            <ResultDescription>
              당신의 여행 스타일은 <strong>{result.type}</strong>입니다.
              <br />
              {result.description}
            </ResultDescription>
            <ResultTags>
              {result.tags.map(tag => (
                <ResultTag key={tag}>{tag}</ResultTag>
              ))}
            </ResultTags>
            <ActionButton onClick={handleStartPlanning}>
              추천 여행지 보기
            </ActionButton>
          </ResultContainer>
        </TestCard>
      </TestContainer>
    );
  }

  const currentQ = questions[currentQuestion];
  const selectedOption = answers[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;

  return (
    <TestContainer>
      <TestCard
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <ProgressBar>
          <ProgressFill
            initial={{ width: 0 }}
            animate={{ width: `${progress}%` }}
            transition={{ duration: 0.5 }}
          />
        </ProgressBar>
        
        <QuestionNumber>
          {currentQuestion + 1} / {questions.length}
        </QuestionNumber>
        
        <QuestionTitle>{currentQ.question}</QuestionTitle>
        
        <OptionsContainer>
          {currentQ.options.map((option, index) => (
            <OptionButton
              key={option.id}
              $selected={selectedOption === option.id}
              onClick={() => handleOptionSelect(option.id)}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
              whileHover={{ scale: 1.02 }}
            >
              {option.text}
              {selectedOption === option.id && (
                <SelectedIcon>
                  <FaCheck />
                </SelectedIcon>
              )}
            </OptionButton>
          ))}
        </OptionsContainer>
        
        <NavigationButtons>
          <NavButton
            onClick={handlePrevious}
            $disabled={currentQuestion === 0}
            disabled={currentQuestion === 0}
          >
            <FaArrowLeft />
            이전
          </NavButton>
          
          <NavButton
            onClick={handleNext}
            $disabled={!selectedOption}
            disabled={!selectedOption}
          >
            {currentQuestion === questions.length - 1 ? '결과 보기' : '다음'}
            <FaArrowRight />
          </NavButton>
        </NavigationButtons>
      </TestCard>
    </TestContainer>
  );
};

export default TravelTestPage;
